  --nav-bg: linear-gradient(-225deg, var(--color-cyan-light) 0, var(--color-pink-light-a7) 100%);
}
.primary {
  --note-border: #cda0c7;
  --note-bg: #fdf8ff;
  --note-text: #8a51c0;
  --note-hover: #935aca;
}
.info {
  --note-border: #8fa4dc;
  --note-bg: #f1f9ff;
  --note-text: #1d4974;
  --note-hover: #1d5fa0;
}
.success {
