  --note-bg: #fffbeb;
  --note-text: #947600;
  --note-hover: #ccb045;
}
.danger {
  --note-border: #f4b3c1;
  --note-bg: #fff2f5;
  --note-text: #cc0f35;
  --note-hover: #f14668;
}
[data-theme="dark"]:root {
  --grey-0: #222;
  --grey-1: #21252b;
